<template>    <div class="vue-zoom-container" v-scale ref="scaleBox">        <div class="vue-zoom-min-box _leftBox" >            <img  class="_minPic">            <div class="_vue-mask"></div>        </div>        <div class="vue-zoom-max-box _rightBox"  >            <img class="_maxPic">        </div>        <div class="l_bottom">            <button class="left_btn"><img src="./right_btn2.png" alt="" style="transform: rotateZ(180deg)"></button>            <div class="img_box">            <ul class="picBox">                <li>                    <img src="http://www.fooju.cn/fjw/uploads/base64/s_basepic_15306891877164.png" alt="">                </li>            </ul>            </div>            <button class="right_btn"><img src="./right_btn2.png" alt=""></button>        </div>    </div></template><script>    export default {        name: "myZoom",        props: {            picArr:{                type: []            }        },        methods: {            get(){                console.log(this.picArr);            },        },        created(){            this.get()        },        directives: {            scale: {                inserted(el,binding,vnode){                    let leftBox = el.querySelector("._leftBox");                    let minPic = el.querySelector("._minPic");                    let mask = el.querySelector("._vue-mask");                    let maxPic = el.querySelector("._maxPic");                    let rightBox = el.querySelector("._rightBox");                    maxPic.src = minPic.src = "http://www.fooju.cn/fjw/uploads/base64/s_basepic_15314563433831.png"                        // binding.value.url;                    function mouseIn(ev) {                        ev = ev || event;                        document.addEventListener("mousemove",mouseMove)                        leftBox.addEventListener("mouseleave",mouseLeave)                    }                    function mouseMove(ev){                        ev = ev || event;                        let scrollTop = document.documentElement.scrollTop || document.body.scrollTop;                        let computedLeft = ev.clientX - el.offsetLeft - mask.offsetWidth/2;                        let computedTop = ev.clientY - el.offsetTop + scrollTop - mask.offsetHeight/2;                        let limitLeft = leftBox.offsetWidth-mask.offsetWidth;                        let limitTop = leftBox.offsetHeight-mask.offsetHeight                        mask.style.display = "block";                        if(computedLeft<0){                            computedLeft = 0;                        }                        else if(computedLeft>limitLeft){                            computedLeft = limitLeft                        }                        if(computedTop<0){                            computedTop = 0;                        }                        else if (computedTop>limitTop){                            computedTop = limitTop;                        }                        mask.style.top = computedTop + "px";                        mask.style.left = computedLeft + "px";                        rightBox.style.display = "block";                        maxPic.style.transform= `translate3d(${-computedLeft*2.5}px,${-computedTop*2.5}px,0)`                    }                    function mouseLeave(ev) {                        ev = ev || event;                        document.removeEventListener("mousemove", mouseMove)                        mask.style.display = "none";                        rightBox.style.display = "none"                    }                    el._move = mouseMove;                    el._in = mouseIn;                    el._leave = mouseLeave;                    leftBox.onmouseenter = mouseIn                },                unbind(el,binding,vnode){                    let leftBox = el.querySelector("._leftBox");                    let minPic = el.querySelector("._minPic");                    let mask = el.querySelector("._vue-mask");                    let maxPic = el.querySelector("._maxPic");                    let rightBox = el.querySelector("._rightBox");                    leftBox.removeEventListener("mouseenter",el._in);                }            },        },    }</script><style scoped>    .vue-zoom-container {        position: relative;    }    .vue-zoom-min-box {        position: relative;        width: 592px;        height: 442px;        overflow: hidden;    }    .vue-zoom-min-box img {        display: block;        width: 100%;        height: 100%;    }    ._vue-mask {        position: absolute;        cursor: move;        width: 237px;        height: 216px;        background: rgba(255,255,0,.2);    }    .vue-zoom-max-box {        display: none;        position: absolute;        left: 102%;        top: 0;        box-shadow: rgb(204, 204, 204) 0px 0px 4px 4px;        width: 592px;        height: 542px;        overflow: hidden;    }    .vue-zoom-max-box img{        display: block;        width: 1480px;        height: 1105px    }    .l_bottom{        width: 592px;        height: 90px;    }     .l_bottom .img_box{        width: 512px;        height: 100px;        overflow: hidden;        float: left;    }    .l_bottom button{        display: inline-block;        width: 30px;        height: 88px;        background-color: #fff;        border: 1px solid #5f1985;        margin-top: 10px;        font-size: 20px;    }    .l_bottom .left_btn{        float: left;        margin-right: 10px;    }     .l_bottom .right_btn{        float: right;        margin-left: 10px;    }     .l_bottom ul{        list-style: none;        display: inline-block;        padding: 0;        margin: 0;        width: 780px;        height: 90px;        overflow: hidden;    }   .l_bottom ul li{        display: inline-block;        width: 120px;        margin-top: 10px;        margin-right: 10px;    }    .l_bottom ul li img{        display:block;        width: 120px;    }</style>